		}
		bool ok = true;
		while (nt != T) {
			nt /= 2;
			if (nt >= asize)continue;
			auto ap = issame(L, L + nt, nt-1);
			if (ap.first)continue;
			else {
				int p(ap.second);
				if (p == -1) {
					ok = false;
					break;
				}
				else {
					if (changeind != -1) {
						ok = false;
						break;
					}
					else if (nt == T) {
						ok = true;
						break;
					}
					else {
						bool lsame = issame(L,L+nt/2,nt/2-1).first;
						bool rflag = false;
						if(L+2*nt>R){
							const int l3 = L + nt;
							int r3 = L + nt + nt / 2 - 1;
							const int l4 = L + nt + nt / 2;
							int r4 = L + nt + nt - 1;
							if (L + nt + nt / 2 > R) {
								rflag = true;
							}
							else {
								if (r4 > R) {
									r4 = R;
									assert(l4 <= R);
									r3 = l3 + (r4 - l4);
								}
								if (r4 < L+nt+p)assert(false);
								if (r3 < L + nt + p&&L + nt + p < l4)rflag = true;
							}

						}
						if (rflag) {
							if (lsame) {
								changeind = L + nt + p;
								changeto = st[L + p];
							}
							else {
								changeind = L + p;
								changeto = st[L + nt + p];
								if (!issame(L, L + nt / 2, nt / 2 - 1).first) {
									ok = false;
									break;
								}
							}
						}
						else {
							if (lsame&&rsame)assert(false);
							else if (lsame) {
								changeind = L + nt + p;
								changeto = st[L + p];
							}
							else if (!lsame) {
								changeind = L + p;
								changeto = st[L + nt + p];
								if (!issame(L, L + nt / 2, nt / 2 - 1).first) {
									ok = false;
									break;
								}
							}
						}
					}
				}
			}
		}
		if (ok)cout << "Yes" << endl;
		else cout << "No" << endl;
	}
	return 0;
}

