typedef unsigned long long ull;
#define MAX_N 100005

ull B=5575777;
ull mem[MAX_N];

ull pow(int x){
  assert(0<=x && x<MAX_N);

  if(x==0)return 1;
  if(mem[x]>0)return mem[x];
  if(x&1)return mem[x]=pow(x-1)*B;
  mem[x]=pow(x/2);
  mem[x]*=mem[x];
  return mem[x];
}

