#include <bits/stdc++.h>
#define MOD 1000000007LL
using namespace std;
typedef long long ll;
typedef pair<int,int> P;
typedef pair<int,P> PP;

string str;

struct rollinghash{
	static const int MD=3;
	static const int MAX=100005;
	const ll base[MD]={1007LL,1009LL,1009LL};
	const ll mods[MD]={999999937LL, 1000000007LL,912941443LL};
	ll hash[MD][MAX+1];
	ll pw[MD][MAX+1];
	void init(){
		for(int i=0;i<MD;i++){
			pw[i][0]=1;
			for(int j=0;j<MAX;j++){
				pw[i][j+1]=pw[i][j]*base[i]%mods[i];
			}
		}
	}
	void construct(const string &str){
		int n=str.size();
		for(int i=0;i<MD;i++){
			hash[i][0]=0;
			for(int j=0;j<n;j++){
				hash[i][j+1]=(hash[i][j]*base[i]%mods[i]+str[j])%mods[i];
			}
		}
	}

	// calc hash value [l,r)
	ll hash_value(int l,int r,int i){
		return ((hash[i][r]-hash[i][l]*pw[i][r-l])%mods[i]+mods[i])%mods[i];
	}

	ll hash_value2(int l,int r,int pos,int v,int i){
		ll val=((hash[i][r]-hash[i][l]*pw[i][r-l])%mods[i]+mods[i])%mods[i];
		if(l<=pos && pos<r){
			ll dif=v-str[pos];
			val+=dif*pw[i][r-pos-1]%mods[i];
			val%=mods[i];
			if(val<0)val+=mods[i];
		}
		return val;
	}

	PP hash_value(int l,int r){
		return PP(hash_value(l,r,0),P(hash_value(l,r,1),hash_value(l,r,2)));
	}



	PP hash_value2(int l,int r,int pos,int v){
		return PP(hash_value2(l,r,pos,v,0),P(hash_value2(l,r,pos,v,1),hash_value2(l,r,pos,v,2)));
	}
};

rollinghash rh; 

bool solve(int l,int r,int t){
	if(rh.hash_value(l,r-t)==rh.hash_value(l+t,r))return true;
	int pl=0,pr=r-l+1;
	while(pl+1<pr){
		int mid=(pl+pr)/2;
		if(rh.hash_value(l,l+mid)==rh.hash_value(l+t,l+t+mid)){
			pl=mid;
		}else{
			pr=mid;
