								assert(issame(L, L + nt / 2, nt / 2 - 1).first);
								assert(issame(L + nt, L + nt + nt / 2, nt / 2 - 1).first);
							}
							else if(rsame){
								changeind = L + p;
								changeto = st[L + nt + p];
								if (!issame(L, L + nt / 2, nt / 2 - 1).first) {
									ok = false;
									break;
								}
								assert(issame(L + nt, L + nt + nt / 2, nt / 2 - 1).first);
							}
							else {
								ok = false;
								break;
							}
						}
						else {
							if (lsame&&rsame)assert(false);
							else if (lsame) {
								changeind = L + nt + p;
								changeto = st[L + p];
								assert(issame(L, L + nt / 2, nt / 2 - 1).first);
								assert(issame(L + nt, L + nt + nt / 2, nt / 2 - 1).first);
							}
							else if (rsame) {
								changeind = L + p;
								changeto = st[L + nt + p];
								if (!issame(L, L + nt / 2, nt / 2 - 1).first) {
									ok = false;
									break;
								}
								assert(issame(L + nt, L + nt + nt / 2, nt / 2 - 1).first);

							}
							else {
								ok = false;
								break;
							}
						}
					}
				}
				assert(issame(L, L + nt, nt - 1).first);
			}
		}
		assert(ok==anss[num]);
		if (ok)cout << "Yes" << endl;
		else cout << "No" << endl;
	}
	return 0;
}

