        cout << "Yes" << endl;
      } else {
        cout << "No" << endl;
      }
      continue;
    }

    if(l1 + lcp1 + 1 + lcp2 < l2) {
      cout << "No" << endl;
      continue;
    }

    if(S[l1 + lcp1] == S[l2 + lcp1 + 1 + lcp2]) {
      cout << "Yes" << endl;
    } else {
      cout << "No" << endl;
    }

/*
