ull get(int l,int r){return S[r]-S[l]*PB[r-l];}
ull get(int l,int r,int x,char ch){
  ull res=get(l,r);
  if(x<l||r<=x) return res;
  return res+ch*PB[r-x-1]-str[x]*PB[r-x-1];
}

int search(int a,int b,int x,int ch){
  int L=0,M,R=1e5+1;
  while(L<R){
    M = (L+R)/2;
    if(max(a,b)+M>n||get(a,a+M,x,ch)!=get(b,b+M,x,ch)) R = M;
    else L = M+1;
  }
  return L-1;
}

bool check(int l,int r,int t){
  int p = l+t+search(l,l+t,-1,0);
  int q = l+(p-l-t)%t;
  return get(l,r-t,p,str[q])==get(l+t,r,p,str[q])||
    get(l,r-t,q,str[p])==get(l+t,r,q,str[p]);
}

