									r4 = r;
									assert(l4 <= r);
								}
								rsame = getha(l3, r3) == getha(l4, r4);
								if (r3 < l + nt + p&&l + nt + p < r3)rflag = true;
							}

						}
						if (rflag) {
							if (lsame) {
								changeind = l2 + p;
								changeto = st[l1 + p];
							}
							else {
								changeind = l1 + p;
								changeto = st[l2 + p];
							}
						}
						else {
							if (lsame&&rsame)assert(false);
							else if (lsame&&!rsame) {
								changeind = l2 + p;
								changeto = st[l1 + p];
							}
							else if (!lsame&&rsame) {
								changeind = l1 + p;
								changeto = st[l2 + p];
							}
							else {
								ok = false;
								break;
							}
						}

					}

				}

			}
		}
		if (ok)cout << "Yes" << endl;
		else cout << "No" << endl;
	}
	return 0;
}

