  cin>>s;

  cal_hash();

  cin>>q;

  int l, r, t;

  for(int i=0;i<q;i++){
    scanf("%d %d %d", &l, &r, &t);
    if(check(l, r, t)) printf("Yes\n");
    else printf("No\n");
  }

  return 0;
}

