using ull = unsigned long long;
struct RollingHash {
  //string s;
  ull base, mod;
  vector<ull> hashed, power;
  RollingHash(const string& s):/*s(s),*/ base(9973ll), mod(999999937ll){
    int sz = s.size();
    hashed.resize(sz+1, 0);
    power.resize(sz+1, 0);
    power[0] = 1;
    for(int i = 0; i < sz; i++) {
      power[i+1] = power[i]*base%mod;
      hashed[i+1] = (hashed[i]*base%mod+s[i])%mod;
    }
  }
  ull get(int l, int r) {
    return (hashed[r]-hashed[l]*power[r-l]%mod+mod)%mod;
  }
  ull concat(ull h1, ull h2, int len) {
    return (h1*power[len]%mod+h2)%mod;
  }
  ull rehash(int l, int r, int i, char c) {
    if(i < l || r <= i) return get(l, r);
    //cout << r-l << " " << s.substr(l, r-l) << " " << s.substr(l, i-l) << endl;
    ull h = get(l, i);
    //cout << l << " " << r << " " << i << " " << c <<  " " << h << endl;
    h = (h*base%mod + c)%mod;
    return concat(h, get(i+1, r), r-(i+1));
  }
};

