#include <bits/stdc++.h>
using namespace std;
#define Long __int128_t
#define int long long
#define REP(i, n) for (int i = 0; i < (n); i++)

Long modpow(Long a, Long n, Long mod) {
    if (n == 0) return 1;
    if (n % 2 == 0) {
        Long t = modpow(a, n / 2, mod);
        return t * t % mod;
    }
    return a * modpow(a, n - 1, mod) % mod;
}

Long modinv(Long a, Long mod) { return modpow(a, mod - 2, mod); }

map<Long, Long> memo[101010];

// a^0 + a^1 + ... + a^{n-1}
Long powsum(Long a, Long n, Long mod) {
    if (memo[n].count(a)) return memo[n][a];
    if (n == 0) return 0;
    if (n % 2 == 0) {
        Long t = powsum(a, n / 2, mod);
        return memo[n][a] = (1 + modpow(a, n / 2, mod)) * t % mod;
    }
    return memo[n][a] = (modpow(a, n - 1, mod) + powsum(a, n - 1, mod)) % mod;
}

template <Long mod, Long p = 257>
struct RollingHash {
    int n;
    vector<Long> table;
    vector<Long> pows;
    RollingHash(const string &s) : n(s.size()), table(n + 1), pows(n + 1) {
        table[0] = 1;
        pows[0] = 1;
        for (int i = 0; i < n; i++) {
            table[i + 1] = (table[i] * p + s[i]) % mod;
            pows[i + 1] = pows[i] * p % mod;
        }
    }

    // [l, r)
    Long hash_substr(int l, int r) {
        Long ret = (table[r] - table[l] * pows[r - l]) % mod;
        if (ret < 0) ret += mod;
        return ret;
    }

    // [sl, sr)[tl, tr) + [tl2, tr2)
    bool is_periodic(int sl, int sr, int tl, int tr, int tl2 = 0, int tr2 = 0) {
        int slen = sr - sl, tlen = tr - tl, tlen2 = tr2 - tl2;
        int k = slen / (tlen + tlen2);
        Long target = (hash_substr(tl, tr) * pows[tlen2] % mod + hash_substr(tl2, tr2)) *
                      powsum(pows[tlen + tlen2], k, mod) % mod;
        int need = slen % (tlen + tlen2);
        target = target * pows[need] % mod;
        if (need <= tlen) {
            target += hash_substr(tl, tl + need);
        } else {
            need -= tlen;
            Long add = hash_substr(tl, tr);
            add = add * pows[need] % mod;
            add += hash_substr(tl2, tl2 + need);
            target += add;
        }
        target %= mod;
        return target == hash_substr(sl, sr);
    }
};

signed main() {
    ios::sync_with_stdio(false);
    cin.tie(0);

    string s;
    cin >> s;

    RollingHash<4611686018427387903> rh(s);

    auto hoge = [&](int l, int r, int t) {
        int len = r - l;
        int L = 0, R = len;
        while (L < R - 1) {
            int M = (L + R) / 2;
            if (rh.is_periodic(l, l + M, l, l + t)) {
                L = M;
            } else {
                R = M;
            }
        }
        int amari = (L + 1) % t;
        return rh.is_periodic(l + L + 1, r, l + amari, l + t, l, l + amari);
    };

    int q;
    cin >> q;
    while (q--) {
        int l, r, t;
        cin >> l >> r >> t;
        l--;
        if (rh.is_periodic(l, r, l, l + t)) {
            cout << "Yes" << '\n';
            continue;
        }

        if (hoge(l, r, t)) {
            cout << "Yes" << '\n';
            continue;
        }

        if (r - l < t * 2) {
            cout << "No" << '\n';
            continue;
        }

        if (!rh.is_periodic(l + t, r, l + t, l + t + t)) {
            cout << "No" << '\n';
            continue;
        }

        if (hoge(l, l + 2 * t, t)) {
            cout << "Yes" << '\n';
            continue;
        }

        cout << "No" << '\n';
    }
}

#include "bits/stdc++.h"
using namespace std;
using ll = long long int;
using ha = pair<ll, ll>;
struct RollingHash {
	static const ll mul0 = 10009, mul1 = 10007;
	static const ll add0 = 1000010007, add1 = 1003333331;
	size_t len;
	vector<ha>hash_;
	static vector<ha>pmo_;
	void init(const vector<ll>& s) {
		len = s.size();
		hash_.resize(len + 1);
		hash_[0] = make_pair(0, 0);

		if (pmo_.empty()) {
			pmo_.push_back(make_pair(1, 1));
		}
		while (pmo_.size() <= len) {
			pmo_.push_back(make_pair(pmo_.back().first*mul0, pmo_.back().second*mul1));
		}
		for (unsigned int i = 0; i < len; ++i) {
			hash_[i + 1] = make_pair((hash_[i].first * mul0 + s[i]), (hash_[i].second*mul1 + s[i]));
		}
		return;
	}
	void init(const string& s) {
		vector<ll>v;
		for (char c : s) {
			v.push_back(c);
		}
		init(v);
	}
	ha hash(const int l, const int r)const { // s[l..r]
		return make_pair(hash_[r + 1].first - hash_[l].first * pmo_[r + 1 - l].first,
			hash_[r + 1].second - hash_[l].second * pmo_[r + 1 - l].second);
	}

	ha get_changed_hash(const int index, const ll from, const ll to, const int l, const int r)const {
		const ha p(hash(l, r));
		if (l > index || r < index)return p;
		return make_pair(
			p.first + (to - from) * pmo_[r + 1 - index - 1].first,
			p.second + (to - from) * pmo_[r + 1 - index - 1].second);
	}
};
vector<ha> RollingHash::pmo_;

RollingHash rh;
int check(const int a, const int b,int len) {
	int al = a, ar = a+ len;
	int bl = b, br = b+len;
	int lans = -1;
	int rans =len;
	while (lans + 1 != rans) {
		int midans = (lans + rans) / 2;
		if (rh.hash(al, al + midans) == rh.hash(bl, bl + midans)) {
			lans = midans;
		}
		else {
			rans = midans;
		}
	}
	if (rh.hash(al + rans + 1, ar) == rh.hash(bl + rans + 1, br))return rans;
	else return -1;
}
