							if (l4 > R) {
								rflag = true;
							}
							else {
								if (r4 > R) {
									r4 = R;
									assert(l4 <= R);
									r3 = l3 + (r4 - l4);
								}
								if (r3 < L + nt + p&&L + nt + p < l4)rflag = true;
							}

						}
						if (rflag) {
						//	assert(rsame);
							if (lsame) {
								changeind = L + nt + p;
								changeto = st[L + p];
							}
							else {
								changeind = L + p;
								changeto = st[L + nt + p];
