							else if (lsame&&!rsame) {
								changeind = L + nt + p;
								changeto = st[L + p];
							}
							else if (!lsame&&rsame) {
								changeind = L + p;
								changeto = st[L + nt + p];
							}
							else {
								ok = false;
								break;
							}
