bool IS_SAME(Info left,Info right){
	if(left.hash_1 == right.hash_1 && left.hash_2 == right.hash_2)return true;
	else{
		return false;
	}
}

Info calcHash(int left,int right){

	Info ret;

	int length = right-left+1;

	//index:T[right]HASH[right+1]
	ret.hash_1 = HASH[right+1][0]-HASH[left][0]*POW[length][0];
	ret.hash_2 = HASH[right+1][1]-HASH[left][1]*POW[length][1];

	return ret;
}

//AB
int calc_match_len(int start_A,int start_B,int length){

	int left = 0,right = length-1,mid = (left+right)/2;
	int match_len = 0;
	Info tmp_A,tmp_B;

	while(left <= right){

		tmp_A = calcHash(start_A,start_A+mid);
		tmp_B = calcHash(start_B,start_B+mid);

		if(IS_SAME(tmp_A,tmp_B)){
			match_len = mid+1;
			left = mid+1;
		}else{
			right = mid-1;
		}
		mid = (left+right)/2;
	}

	return match_len;
}

Info replace(Info X,int length,int loc,char pre,char next){

	X.hash_1 -= pre*POW[length-(loc+1)][0];
	X.hash_1 += next*POW[length-(loc+1)][0];

	X.hash_2 -= pre*POW[length-(loc+1)][1];
	X.hash_2 += next*POW[length-(loc+1)][1];

	return X;
}


