char changeto = 0;
ha getha(const int l, const int r) {
	if (changeind != -1) {
		return rh.get_changed_hash(changeind, st[changeind], changeto, l, r);
	}
	else {
		ha h = rh.hash(l, r);
		return h;
	}
}
int L, R, T;
pair<bool, int> issame(int a, int b, int alen) {
	if (b > R) {
		return make_pair(true, -1);
	}
	else {
		assert(b <= R);
		int len = min(alen, R - b);
		bool same = getha(a, a + len) == getha(b, b + len);
		if (same)return make_pair(true, -1);
		else {

			int acheck = check(a,b,len);
			return make_pair(false, acheck);
		}
	}
}
int main() {
	cin >> st;
	rh.init(st);
	int N; cin >> N;
	while (N--) {
		changeind = -1;
		changeto = -1; cin >> L >> R >> T;
		L--; R--;
		int asize = R - L + 1;
		int nt = T;
		while (asize * 2 >= nt) {
			nt *= 2;
		}
		bool ok = true;
		while (nt != T) {
			nt /= 2;
			if (nt >= asize)continue;
			auto ap = issame(L, L + nt, nt-1);
			if (ap.first)continue;
			else {
				int p(ap.second);
				if (p == -1) {
					ok = false;
					break;
				}
				else {
					if (changeind != -1) {
						ok = false;
						break;
					}
					else if (nt == T) {
						ok = true;
						break;
					}
					else {
						bool lsame = issame(L,L+nt/2,nt/2-1).first;
						bool rsame= issame( L + nt,L+nt+nt/2, nt / 2 - 1).first;
						bool rflag = false;
						if(L+2*nt>R){
							const int l3 = L + nt;
							int r3 = L + nt + nt / 2 - 1;
							const int l4 = L + nt + nt / 2;
							int r4 = L + nt + nt - 1;
							if (L + nt + nt / 2 > R) {
								rflag = true;
							}
							else {
								if (r4 > R) {
									r4 = R;
									assert(l4 <= R);
									r3 = l3 + (r4 - l4);
								}
								if (r4 < p)assert(false);
								if (r3 < L + nt + p&&L + nt + p < l4)rflag = true;
							}

						}
						if (rflag) {
							assert(!lsame||rsame);
							if (lsame) {
								changeind = L + nt + p;
								changeto = st[L + p];
							}
							else {
								changeind = L + p;
								changeto = st[L + nt + p];
							}
						}
						else {
							if (lsame&&rsame)assert(false);
							else if (lsame&&!rsame) {
								changeind = L + nt + p;
								changeto = st[L + p];
							}
							else if (!lsame&&rsame) {
								changeind = L + p;
								changeto = st[L + nt + p];
							}
							else {
								ok = false;
								break;
							}
						}
					}
				}
			}
		}
		if (ok)cout << "Yes" << endl;
		else cout << "No" << endl;
	}
	return 0;
}

#include<bits/stdc++.h>
using namespace std;
typedef unsigned long long ull;
#define MAX_N 100005

ull B=5575777;
ull mem[MAX_N];

ull pow(int x){
  if(x==0)return 1;
  if(mem[x]>0)return mem[x];
  if(x&1)return mem[x]=pow(x-1)*B;
  mem[x]=pow(x/2);
  mem[x]*=mem[x];
  return mem[x];
}

int n,q,l,r,t;
char s[MAX_N];
ull p[MAX_N];

ull get(int l,int r){
  return p[r]-p[l]*pow(r-l);
}

ull get(int l,int r,int x,char ch){
  ull res=p[r]-p[l]*pow(r-l);
  if(l<=x&&x<r){
    int dist=r-x-1;
    res-=pow(dist)*s[x];
    res+=pow(dist)*ch;
  }
  return res;
}

bool eq(int a,int b,int l,int r){
  return ( get(a,b) == get(l,r) );
}

bool eq(int a,int b,int l,int r, int x,char ch){
  return ( get(a,b,x,ch) == get(l,r,x,ch) );
}

bool calc(int a,int b,int l,int r){
  if(b-a!=r-l)return false;
  int L=0,R=b-a+1,M;
  while(R-L>1){
    M=(L+R)/2;
    if( eq(a,a+M,l,l+M) ){
      L=M;
    }else{
      R=M;
    }
  }
  if(L==b-a)return true;
  return ( eq(a,b,l,r, l+L, s[a+L]) || eq(a,b,l,r, a+L, s[l+L] )  );
}


bool solve(){
  int len=r-l;
  if(t==len)return true;
  if(t*2>=len){
    return calc(l,l+(len-t),r-(len-t),r);
  }
  int as=len%t;
  if( calc(l,r-as-t,l+t,r) )return true;  
  return false;
}

int main(){
  scanf("%s",s);
  n=strlen(s);

  p[0]=0;
  for(int i=1;i<=n;i++){
    p[i]=p[i-1]*B+s[i-1];
  }

  scanf("%d",&q);
  while(q--){
    scanf("%d %d %d",&l,&r,&t);

    l--;

    if(solve())printf("Yes\n");
    else printf("No\n");
  }
  return 0;
}

