		}
		bool ok = true;
		while (nt != T) {
			nt /= 2;
			if (nt >= asize)continue;
			auto aa = issame(make_pair(L, L + nt-1), make_pair(L + nt, L + nt * 2 - 1));
			if (aa.first)continue;
			else {
				int p(aa.second);
				if (p == -1) {
					ok = false;
					break;
				}
				else {
					if (changeind != -1) {
						ok = false;
						break;
					}else if (nt == T) {
						ok = true;
						break;
					}
					else {
						bool lsame = getha(l1, l1 + nt / 2 - 1) == getha(l1 + nt / 2, l1+nt-1);
						bool rsame;
						bool rflag = false;
						{
							const int l3 = L + nt;
							int r3 = L + nt + nt / 2 - 1;
							const int l4 = L + nt + nt / 2;
							int r4 = L + nt + nt - 1;
							if (l4 > R) {
								rsame = true;
								rflag = true;
							}
							else {
								if (r4 > R) {
									r4 = R;
									assert(l4 <= R);
									r3 = l3 + (r4 - l4);
								}
								rsame = getha(l3, r3) == getha(l4, r4);
								if (r3 < L + nt + p&&L + nt + p < l4)rflag = true;
							}

						}
						if (rflag) {
							if (lsame) {
								changeind = l2 + p;
								changeto = st[l1 + p];
							}
							else {
								changeind = l1 + p;
								changeto = st[l2 + p];
							}
						}
						else {
							if (lsame&&rsame)assert(false);
							else if (lsame&&!rsame) {
								changeind = l2 + p;
								changeto = st[l1 + p];
							}
							else if (!lsame&&rsame) {
								changeind = l1 + p;
								changeto = st[l2 + p];
							}
							else {
								ok = false;
								break;
							}
						}

					}

