        return hash[r + 1] - hash[l] * base[r - l + 1];
    }

    // ll+tsize
    int comp(int l, int t, int size) {
        if (getHash(l, l + size - 1) == getHash(l+t, l+t + size - 1)) {
//            log.printf("comp(%d %d %d) = -1\n", l, t, size, -1);
            return -1;
