void log(A...)(A arg) {
    stderr.writeln(arg);
}
int size(T)(in T s) {
    return cast(int)s.length;
}

long modPow(long mod, long x, long n) {
    x %= mod;
    if (n == 0) return 1;
    if (n % 2 == 0) return modPow(mod, x * x % mod, n / 2);
    return x * modPow(mod, x % mod, n - 1) % mod;
}

long extgcd(long a, long b, ref long x, ref long y) {
    long g = a; x = 1; y = 0;
    if (b != 0) {
        g = extgcd(b, a % b, y, x);
        y -= (a / b) * x;
    }
    return g;
}
long inverse(long mod, long v) {
    long x, y;
    if (extgcd(v, mod, x, y) == 1) return (x + mod) % mod;
    return 0;
}

class RollingHash {
    long[] H;
    long mod;
    long P;
    this(long P, long mod, in string s) { 
        this.mod = mod;
        this.P = P;
        auto N = s.length;
        this.H = new long[N + 1]; // H[k]: s[0]*P^(n-1) + s[1]*P^(n-2) + ... + s[n-1]*P^0
        foreach (int i, c; s) {
            H[i + 1] = (H[i] * P + cast(int)(c)) % mod;
        }
    }
    long hash(int l, int r) { // [l, r)
        return (H[r] - H[l] * modPow(mod, P, r - l) % mod + mod) % mod;
    }
}

const long P = 10000481L;
const long[] mods = [1000000123L, 1000000207L, 1000000363L];
void main() {
    auto s = readln.chomp;
    int N = s.size;
    int Q = readln.chomp.to!int;

    auto P_exp = new bool[long][mods.length];
    foreach (k, mod; mods) {
        long p = 1;
        P_exp[k][p] = true;
        foreach (n; 0 .. N + 2) {
            p = (p * P) % mod;
            P_exp[k][p] = true;
        }
    }

    auto hashers = iota(0, mods.length, 1).map!((k) => new RollingHash(P, mods[k], s)).array;

    foreach (_; 0 .. Q) {
        int l, r, t; 
        scanf("%d %d %d\n", &l, &r, &t);
        l--;

        auto k = (r - l) / t;
        auto m = r - l - t * k;
        //log([l, r, t, k, m]);

        auto ans = new bool[hashers.length];
        foreach (j, ref hasher; hashers) {
            auto mod = hasher.mod;
            assert(mod == mods[j]);
            long org = hasher.hash(l, r);
            auto pow = delegate long(long x, long n) { return modPow(mod, x, n); };
            auto inv = delegate long(long x) { return inverse(mod, x); };
            auto Pt = pow(P, t);

            long cycle_hash(int z) {
                long a = hasher.hash(l + t * z, l + t * z + t);
                return (a * ( (pow(Pt, k) - 1 + mod) % mod ) % mod * inv( (pow(P, t) - 1 + mod) % mod ) % mod * pow(P, m) % mod 
                        + hasher.hash(l + t*z, l + t*z + m)) % mod;
            }

            foreach (z; [0, 1]) {
                if (l + t * z + t > r) continue;
                auto diff = (org - cycle_hash(z) + mod) % mod;
                if (diff == 0) { ans[j] = true; }
                foreach (q; -26 .. 26 + 1) {
                    long y = q < 0 ? q + mod : q;
                    if (inv(y) * diff % mod in P_exp[j]) {
                        ans[j] = true;
                    }
                }
            }
        }
        writeln(ans.all ? "Yes" : "No");
    }
}

