int size(T)(in T s) {
    return cast(int)s.length;
}

long modPow(long mod, long x, long n) {
    x %= mod;
    if (n == 0) return 1;
    if (n % 2 == 0) return modPow(mod, x * x % mod, n / 2);
    return x * modPow(mod, x % mod, n - 1) % mod;
}

long extgcd(long a, long b, ref long x, ref long y) {
    long g = a; x = 1; y = 0;
    if (b != 0) {
        g = extgcd(b, a % b, y, x);
        y -= (a / b) * x;
    }
    return g;
}
long inverse(long mod, long v) {
    long x, y;
    if (extgcd(v, mod, x, y) == 1) return (x + mod) % mod;
    return 0;
}

class RollingHash {
    long[] H;
    long mod;
    long P;
    this(long P, long mod, in string s) { 
        this.mod = mod;
        this.P = P;
        auto N = s.length;
        this.H = new long[N + 1]; // H[k]: s[0]*P^(n-1) + s[1]*P^(n-2) + ... + s[n-1]*P^0
        foreach (int i, c; s) {
            H[i + 1] = (H[i] * P + cast(int)(c)) % mod;
        }
    }
    long hash(int l, int r) { // [l, r)
        return (H[r] - H[l] * modPow(mod, P, r - l) % mod + mod) % mod;
    }
}

const long P = 10000481L;
const long[] mods = [1000000123L, 1000000207L, 1000000363L];
void main() {
    auto s = readln.chomp;
    int N = s.size;
    int Q = readln.chomp.to!int;

    auto P_exp = new bool[long][mods.length];
    foreach (k, mod; mods) {
        long p = 1;
        P_exp[k][p] = true;
        foreach (n; 0 .. N + 2) {
            p = (p * P) % mod;
            P_exp[k][p] = true;
        }
    }

    auto hashers = iota(0, mods.length, 1).map!((k) => new RollingHash(P, mods[k], s)).array;

    foreach (_; 0 .. Q) {
        int l, r, t; 
        scanf("%d %d %d\n", &l, &r, &t);
        l--;

        auto k = (r - l) / t;
        auto m = r - l - t * k;
        //log([l, r, t, k, m]);

        auto ans = new bool[hashers.length];
        foreach (j, ref hasher; hashers) {
            auto mod = hasher.mod;
            assert(mod == mods[j]);
            long org = hasher.hash(l, r);
            auto pow = delegate long(long x, long n) { return modPow(mod, x, n); };
            auto inv = delegate long(long x) { return inverse(mod, x); };
            auto Pt = pow(P, t);

            long cycle_hash(int z) {
                long a = hasher.hash(l + t * z, l + t * z + t);
                return (a * ( (pow(Pt, k) - 1 + mod) % mod ) % mod * inv( (pow(P, t) - 1 + mod) % mod ) % mod * pow(P, m) % mod 
                        + hasher.hash(l + t*z, l + t*z + m)) % mod;
            }

            foreach (z; [0, 1]) {
                if (l + t * z + t > r) continue;
                auto diff = (org - cycle_hash(z) + mod) % mod;
                if (diff == 0) { ans[j] = true; }
                foreach (q; -26 .. 26 + 1) {
                    long y = q < 0 ? q + mod : q;
                    if (inv(y) * diff % mod in P_exp[j]) {
                        ans[j] = true;
                    }
                }
            }
        }
        writeln(ans.all ? "Yes" : "No");
    }
}

import sys
readline = sys.stdin.readline
write = sys.stdout.write
def solve():
    base = 37; MOD = 10**9 + 9
    S = readline().strip()
    L = len(S)
    H = [0]*(L+1)
    pw = [1]*(L+1)
    ca = ord('a')
    v = 0
    for i in range(L):
        H[i+1] = v = (v * base + (ord(S[i]) - ca)) % MOD
    v = 1
    for i in range(1, L+1):
        pw[i] = v = v * base % MOD
    Q = int(readline())
    for i in range(Q):
        l, r, t = map(int, readline().split()); l -= 1
        m = r-l
        if ((H[r]-H[r-t]) - (H[l+t]-H[l]) * pw[m-t]) % MOD == 0:
            write("Yes\n")
            continue
        left = 0; right = m-t+1
        base = H[l] - H[l+t]
        while left+1 < right:
            mid = (left + right) >> 1
            if ((H[l+mid]-H[l+t+mid]) - base * pw[mid]) % MOD == 0:
                left = mid
            else:
                right = mid
        l1 = left
        left = 0; right = m-t-l1+1
        base = (H[r-t]-H[r]) % MOD
        while left+1 < right:
            mid = (left + right) >> 1
            if (H[r-t-mid]-H[r-mid]) * pw[mid] % MOD == base:
                left = mid
            else:
                right = mid
        l2 = left
        if l1+l2+1 == m-t:
            if l1 <= t or l2 <= t:
                write("Yes\n")
            else:
                write("No\n")
        else:
            if ((H[l+m-t-l2-1]-H[l+m-l2-1]) - (H[l+l1+1]-H[l+t+l1+1]) * pw[m-t-l2-l1-2]) % MOD != 0:
                write("No\n")
            else:
                p1 = l1; p2 = m-t-l2-1
                if p2 - p1 == t and S[l+p2-t] == S[l+p2+t]:
                    write("Yes\n")
                else:
                    write("No\n")
solve()
