#include<bits/stdc++.h>
#define N 3005
using namespace std;
int F[N][N],ymax,xmax,ans;
int dy[4]={-1,0,1,0};
int dx[4]={0,1,0,-1};

void dfs(int y,int x,int cnt,int dir){
  if(F[y][x]>=0){
    int tmp=F[y][x];
    int ny=y+dy[tmp];
    int nx=x+dx[tmp];
    ans=max(ans,cnt+1);
    if(ny<0||nx<0||ymax<=ny||xmax<=nx)return;
    F[y][x]=-1;
    dfs(ny,nx,cnt+1,tmp);
    F[y][x]=tmp;
    return;
  }
  int ny=y+dy[dir];
  int nx=x+dx[dir];
  if(ny<0||nx<0||ymax<=ny||xmax<=nx)return;
  dfs(ny,nx,cnt,dir);
}

int main(){
  int n,x[N],y[N];
  char d[N];
