  for(int i=0;i<n;i++){
    cin>>x[i]>>y[i]>>d[i];
    X[x[i]]=0;
    Y[y[i]]=0;
  }
  int num=0;
  map<int,int>::iterator ite=X.begin();
  while(ite!=X.end()){
    (*ite).second=num;
    num++;
    ite++;
  }
  xmax=num;
  ite=Y.begin();
  num=0;
  while(ite!=Y.end()){
    (*ite).second=num;
    num++;
    ite++;
  }
  ymax=num;
  memset(F,-1,sizeof(F));
  for(int i=0;i<n;i++){
    if(d[i]=='^')F[Y[y[i]]][X[x[i]]]=0;
    if(d[i]=='>')F[Y[y[i]]][X[x[i]]]=1;
    if(d[i]=='v')F[Y[y[i]]][X[x[i]]]=2;
    if(d[i]=='<')F[Y[y[i]]][X[x[i]]]=3;
