int main() {
    cin >> n;
    REP(i,n){
        int x, y, dir;
        char d;
        cin >> x >> y >> d;
        if(d == '>') dir = 0;
        if(d == 'v') dir = 1;
        if(d == '<') dir = 2;
        if(d == '^') dir = 3;
