        vy[y].insert({x,dir});
        pos[i] = {{x,y},dir};
    }
    REP(i,n){
        auto xs = vx, ys = vy;
        int px = pos[i].first.first, py = pos[i].first.second, dir = pos[i].second, cnt = 1;
        while(1){
            xs[px].erase({py,dir});
            ys[py].erase({px,dir});
            if(dir == 0){
                auto p = ys[py].upper_bound({px,-1});
                if(p==ys[py].end())break;
                px = (*p).first;
                dir = (*p).second;
                cnt++;
            }
            else if(dir == 1){
                auto p = xs[px].upper_bound({py,-1});
                if(p==xs[px].end())break;
                py = (*p).first;
                dir = (*p).second;
                cnt++;
            }
            else if(dir == 2){
                if(ys[py].size()==0) break;
                auto p = ys[py].upper_bound({px,-1});
                if(p==ys[py].begin())break;
                p--;
                px = (*p).first;
                dir = (*p).second;
                cnt++;
            }
            else if(dir == 3){
                if(xs[px].size()==0) break;
                auto p = xs[px].upper_bound({py,-1});
                if(p==xs[px].begin())break;
                p--;
                py = (*p).first;
                dir = (*p).second;
                cnt++;
            }
        }
        ans = max(ans, cnt);
    }
    cout << ans << endl;
    return 0;
}

