}

int calc(int y,int x,vector<vector<int> >mp){
  int res=0;
  while(1){
    res++;
    int i=mp[y][x];
    mp[y][x]=-1;
    while(1){
      y+=dy[i];
      x+=dx[i];
      if(y<0||x<0||x>=w||y>=h)return res;    
      if(mp[y][x]!=-1)break;;
    }
  }
  assert(0);
}

