    if(ch=='^')d[i]=0;
    if(ch=='>')d[i]=1;
    if(ch=='v')d[i]=2;
    if(ch=='<')d[i]=3;
  }
  h=compless(Y,y);
  w=compless(X,x);
  vector<vector<int> >mp(h,vector<int>(w,-1));
  for(int i=0;i<X.size();i++)mp[y[Y[i]]][x[X[i]]]=d[i];
  int ans=1;
  for(int i=0;i<h;i++)
    for(int j=0;j<w;j++)
      if(mp[i][j]!=-1)ans=max(ans,calc(i,j,mp));
  cout <<ans<<endl;

  return 0;
}

