  for(int i=0;i<n;i++) cin>>x[i]>>y[i]>>d[i];
  int INF=1LL<<55LL;
  map<P,int> m;
  for(int i=0;i<n;i++) m[P(x[i],y[i])]=i;
  vector<int> cx,cy;
  cx.push_back(INF);
  cx.push_back(-INF);
  cy.push_back(INF);
  cy.push_back(-INF);
  for(int i=0;i<n;i++){
    cx.push_back(x[i]);
    cy.push_back(y[i]);
  }
  sort(cx.begin(),cx.end());
  cx.erase(unique(cx.begin(),cx.end()),cx.end());
  sort(cy.begin(),cy.end());
  cy.erase(unique(cy.begin(),cy.end()),cy.end());
  map<int,int> rx,ry;
  for(int i=0;i<(int)cx.size();i++) rx[cx[i]]=i;
  for(int i=0;i<(int)cy.size();i++) ry[cy[i]]=i;
  vector<set<int> > tx(cx.size()),ty(cy.size());
  for(auto& s:tx){
    s.insert(ry[INF]);
    s.insert(ry[-INF]);
  }
  for(auto& s:ty){
    s.insert(rx[INF]);
    s.insert(rx[-INF]);
  }
  for(int i=0;i<n;i++){
    tx[rx[x[i]]].insert(ry[y[i]]);
    ty[ry[y[i]]].insert(rx[x[i]]);
  }
  int ans=0;
  for(int i=0;i<n;i++){
    vector<set<int> > sx=tx,sy=ty;
    int px=rx[x[i]],py=ry[y[i]];
    char pd=d[i];
    int tmp=0;
    while(1){
      tmp++;
      int bx=px,by=py;
      auto latte=sx[px].find(py);
      auto malta=sy[py].find(px);
      if(pd=='>') ++malta;
      if(pd=='<') --malta;
      if(pd=='v') ++latte;
      if(pd=='^') --latte;
      px=*malta;
      py=*latte;
      pd=d[m[P(cx[px],cy[py])]];
      if(abs(cx[px])==INF||abs(cy[py])==INF) break;
      sx[bx].erase(by);
      sy[by].erase(bx);
    }
    ans=max(ans,tmp);
  }
  cout<<ans<<endl;
  return 0;
}

#include <bits/stdc++.h>
using namespace std;

int solve(int x, int y, vector<set<int>>& vx, vector<set<int>>& vy, char d, vector<vector<char>> const& dd) {
    if(d == '>') {
        auto it = vy[y].upper_bound(x);
        if(it == vy[y].end()) {
            return 1;
        }
        int nx = (*it);
        char nd = dd[*it][y];
        vy[y].erase(it);
        vx[nx].erase(y);
        return solve(nx, y, vx, vy, nd, dd) + 1;
    } else if(d == '<') {
        auto it = vy[y].lower_bound(x);
        if(it == vy[y].begin()) {
            return 1;
        }
        int nx = *(--it);
        char nd = dd[nx][y];
        vy[y].erase(it);
        vx[nx].erase(y);
        return solve(nx, y, vx, vy, nd, dd) + 1;
    } else if(d == 'v') {
        auto it = vx[x].upper_bound(y);
        if(it == vx[x].end()) {
            return 1;
        }
        int ny = *it;
        char nd = dd[x][ny];
        vx[x].erase(ny);
        vy[ny].erase(x);
        return solve(x, ny, vx, vy, nd, dd) + 1;
    } else {
        auto it = vx[x].lower_bound(y);
        if(it == vx[x].begin()) {
            return 1;
        }
        int ny = *(--it);
        char nd = dd[x][ny];
        vx[x].erase(ny);
        vy[ny].erase(x);
        return solve(x, ny, vx, vy, nd, dd) + 1;
    }
}

