void to_unique(vector<int> &v) {
	sort(v.begin(), v.end());
	v.erase(unique(v.begin(), v.end()), v.end());
}

int n;
void solve() {
	cin >> n;
	vector<P> v(n);
	vector<char> d(n);
	rep(i, n) {
		cin >> v[i].first >> v[i].second >> d[i];
	}
	vector<int> vx, vy;
	{
		rep(i, n) {
			vx.push_back(v[i].first);
			vy.push_back(v[i].second);
		}
		to_unique(vx), to_unique(vy);
		map<int, int> mx, my;
		rep(i, vx.size()) {
			mx[vx[i]] = i;
		}
		rep(i, vy.size()) {
			my[vy[i]] = i;
		}
		rep(i, n) {
			v[i].first = mx[v[i].first];
			v[i].second = my[v[i].second];
		}
	}
	map<P, int> mp;
	rep(i, n) {
		mp[v[i]] = i;
	}
	vector<set<int>> stx(vx.size()),sty(vy.size());
	rep(i, n) {
		stx[v[i].first].insert(v[i].second);
		sty[v[i].second].insert(v[i].first);
	}
	int ans = 0;
	rep(i, n) {
		vector<set<int>> sx = stx, sy = sty;
		int cx = v[i].first, cy = v[i].second;
		int cnt = 0;
		while (true) {
			cnt++;
			sx[cx].erase(cy);
			sy[cy].erase(cx);
			int id = mp[{cx, cy}];
			if (d[id] == '>') {
				sy[cy].insert(cx);
				auto itr = sy[cy].find(cx);
				itr++;
				if (itr == sy[cy].end())break;
				int nx = *itr;
				sy[cy].erase(cx);
				cx = nx;
			}
			else if (d[id] == '<') {
				sy[cy].insert(cx);
				auto itr = sy[cy].find(cx);
				if (itr == sy[cy].begin())break;
				itr--;
				int nx = *itr;
				sy[cy].erase(cx);
				cx = nx;
			}
			else if (d[id] == 'v') {
				sx[cx].insert(cy);
				auto itr = sx[cx].find(cy);
				itr++;
				if (itr == sx[cx].end())break;
				int ny = *itr;
				sx[cx].erase(cy);
				cy = ny;
			}
			else {
				sx[cx].insert(cy);
				auto itr = sx[cx].find(cy);
				if (itr == sx[cx].begin())break;
				itr--;
				int ny = *itr;
				sx[cx].erase(cy);
				cy = ny;
			}
		}
		ans = max(ans, cnt);
	}
	cout << ans << endl;
}

