int solve(){
  int res=0;
  FOR(i,0,n){
    FOR(j,0,n){
      if(i==j)continue;
      if(c[i]==">" && b[i]==b[j] && a[i]<a[j])e[i].pb(mp(a[j]-a[i],j));
      if(c[i]=="<" && b[i]==b[j] && a[j]<a[i])e[i].pb(mp(a[i]-a[j],j));
      if(c[i]=="v" && a[i]==a[j] && b[i]<b[j])e[i].pb(mp(b[j]-b[i],j));
      if(c[i]=="^" && a[i]==a[j] && b[j]<b[i])e[i].pb(mp(b[i]-b[j],j));
    }
    sort(all(e[i]));
  }
  FOR(i,0,n){
    fill(used,used+MAX,false);
    res=max(res,dfs(i));
  }
  return res;
}

