#include <bits/stdc++.h>
#define N 3001
using namespace std;
typedef pair<int,int> P;
int W,H,n;
char mp[N+100][N+100];

int compress(int *x1,int w){
  vector <int>xs;
  for(int i=0;i<n;i++)xs.push_back(x1[i]);
  sort(xs.begin(),xs.end());
  xs.erase(unique(xs.begin(),xs.end()),xs.end());

  for(int i=0;i<n;i++)
    x1[i]=find(xs.begin(),xs.end(),x1[i])-xs.begin();
  return xs.size();
}

int dfs(int x,int y,int dir){
  if(x<0||y<0||x>=W||y>=H)return 0;
  int dx[]={0,0,-1,1},dy[]={-1,1,0,0};
  char ch=mp[y][x];
  mp[y][x]=0;

  if(ch=='^')dir=0;
  if(ch=='v')dir=1;
  if(ch=='<')dir=2;
  if(ch=='>')dir=3;
  int res= dfs(x+dx[dir],y+dy[dir],dir)+(ch!=0);
  mp[y][x]=ch;
  return res;
}

int main(){
  cin>>n;
  int x[N],y[N];
  char ch[N];
  for(int i=0;i<n;i++)cin>>x[i]>>y[i]>>ch[i];  
  W=compress(x,1e9);
  H=compress(y,1e9);
  memset(mp,0,sizeof(mp));
  for(int i=0;i<n;i++)mp[y[i]][x[i]]=ch[i];

  int ans=0;
  for(int i=0;i<H;i++)
    for(int j=0;j<W;j++)
      if(mp[i][j]!=0)ans=max(ans,dfs(j,i,0));
  cout <<ans<<endl;
  return 0;
}

#include<bits/stdc++.h>
using namespace std;
typedef pair<int,int> P;

#define MAX_N 3005
int n;
int px[MAX_N],py[MAX_N];
char ch[MAX_N];

int solve(int si){
  bool used[MAX_N]={};
  set< P > sx[MAX_N],sy[MAX_N];
  for(int i=0;i<n;i++){
    if(i==si)continue;
    sx[ px[i] ].insert( P(py[i],i) );
    sy[ py[i] ].insert( P(px[i],i) );
  }
  int res=1;
  int x=px[si],y=py[si];
  char dir=ch[si];

  set< P > :: iterator it;
  while(1){
    P next;
    if(dir=='v'){
      it= sx[x].lower_bound( P(y,0) );
      if(it==sx[x].end())break;
      next=*it;
    }else if(dir=='^'){
      it= sx[x].lower_bound( P(y,0) );
      if(it==sx[x].begin())break;
      it--;
      next=*it;
    }else if(dir=='>'){
      it= sy[y].lower_bound( P(x,0) );
      if(it==sy[y].end())break;
      next=*it;      
    }else if(dir=='<'){
      it= sy[y].lower_bound( P(x,0) );
      if(it==sy[y].begin())break;
      it--;
      next=*it;
    }
    res++;
    int id=next.second;
    x=px[id];
    y=py[id];
    dir=ch[id];
    sx[ x ].erase(P(y,id));
    sy[ y ].erase(P(x,id));
  }
  return res;
}

int main(){
  vector<int> X,Y;
  cin>>n;
  for(int i=0;i<n;i++){
    cin>>px[i]>>py[i]>>ch[i];
    X.push_back(px[i]);
    Y.push_back(py[i]);
  }
  sort(X.begin(),X.end());
  sort(Y.begin(),Y.end());
  X.erase(unique(X.begin(),X.end()),X.end());
  Y.erase(unique(Y.begin(),Y.end()),Y.end());

  for(int i=0;i<n;i++){
    px[i]=lower_bound(X.begin(),X.end(),px[i])-X.begin();
    py[i]=lower_bound(Y.begin(),Y.end(),py[i])-Y.begin();
  }



  int ans=0;
  for(int i=0;i<n;i++){
    ans=max(ans,solve(i));
  }
  cout<<ans<<endl;
  return 0;
}

