using namespace std;


#define NUM 3000

enum DIR{
	North,
	East,
	South,
	West,
};

struct Info{
	int x,y;
	DIR dir;
};

struct Data{
	int index;
	DIR dir;
	bool exist,visited;
};

struct State{
	void set(int arg_y,int arg_x,DIR arg_dir){
		y = arg_y;
		x = arg_x;
		dir = arg_dir;
	}
	int x,y;
	DIR dir;
};

int N;
map<int,int> X_MAP,Y_MAP;
vector<int> Y,X;
int ADJ[NUM][4];
bool visited[NUM];
Info info[NUM];
Data data[NUM][NUM]; //2

int main(){

	scanf("%d",&N);

	char buf[2];

	for(int i = 0; i < N; i++){
		scanf("%d %d %s",&info[i].x,&info[i].y,buf);
		X.push_back(info[i].x);
		Y.push_back(info[i].y);
		switch(buf[0]){
		case '^':
			info[i].dir = North;
			break;
		case '>':
			info[i].dir = East;
			break;
		case 'v':
			info[i].dir = South;
			break;
		case '<':
			info[i].dir = West;
			break;
		}
	}

	sort(X.begin(),X.end());
	sort(Y.begin(),Y.end());

	X.erase(unique(X.begin(),X.end()),X.end());
	Y.erase(unique(Y.begin(),Y.end()),Y.end());

	//
	for(int i = 0; i < X.size(); i++){
		X_MAP[X[i]] = i;
	}
	for(int i = 0; i < Y.size(); i++){
		Y_MAP[Y[i]] = i;
	}

	int size_X = (int)X.size(),size_Y = (int)Y.size();

	for(int i = 0; i < size_X; i++){
		for(int k = 0; k < size_Y; k++)data[i][k].exist = false;
	}

	//
	for(int i = 0; i < N; i++){
		for(int k = 0; k < 4; k++){
			ADJ[i][k] = -1; //
		}

		data[Y_MAP[info[i].y]][X_MAP[info[i].x]].exist = true;
		data[Y_MAP[info[i].y]][X_MAP[info[i].x]].dir = info[i].dir;
		data[Y_MAP[info[i].y]][X_MAP[info[i].x]].index = i;
	}

	//
	for(int row = 0; row < size_Y; row++){
		for(int col = 0; col < size_X; col++){
			if(data[row][col].exist == false)continue;

			//
			for(int tmp_row = row-1; tmp_row >= 0; tmp_row--){
				if(data[tmp_row][col].exist){
					ADJ[data[row][col].index][North] = data[tmp_row][col].index;
					break;
				}
			}

			//
			for(int tmp_col = col+1; tmp_col <= size_X-1; tmp_col++){
				if(data[row][tmp_col].exist){
					ADJ[data[row][col].index][East] = data[row][tmp_col].index;
					break;
				}
			}

			//
			for(int tmp_row = row+1; tmp_row <= size_Y-1; tmp_row++){
				if(data[tmp_row][col].exist){
					ADJ[data[row][col].index][South] = data[tmp_row][col].index;
					break;
				}
			}
			//
			for(int tmp_col = col-1; tmp_col >= 0; tmp_col--){
				if(data[row][tmp_col].exist){
					ADJ[data[row][col].index][West] = data[row][tmp_col].index;
					break;
				}
			}
		}
	}

	int ans = 0;
	int tmp_count,next_index;
	DIR dir;

	for(int start = 0; start < N; start++){
		for(int i = 0; i < N; i++)visited[i] = false;
		visited[start] = true;

		dir = info[start].dir;

		tmp_count = 1;
		next_index = start;

		while(true){

			switch(dir){
			case North:
				while(next_index != -1 && visited[next_index] == true){
					next_index = ADJ[next_index][North];
				}
				break;
			case East:
				while(next_index != -1 && visited[next_index] == true){
					next_index = ADJ[next_index][East];
				}
				break;
			case South:
				while(next_index != -1 && visited[next_index] == true){
					next_index = ADJ[next_index][South];
				}
				break;
			case West:
				while(next_index != -1 && visited[next_index] == true){
					next_index = ADJ[next_index][West];
				}
				break;
			}

			if(next_index == -1)break;

			dir = info[next_index].dir;
			visited[next_index] = true;

			tmp_count++;
		}

		ans = max(ans,tmp_count);
	}

	printf("%d\n",ans);

	return 0;
}

#include <bits/stdc++.h>
#define N 3001
using namespace std;
typedef pair<int,int> P;
int W,H,n;
char mp[N+100][N+100];

int compress(int *x1,int w){
  vector <int>xs;
  for(int i=0;i<n;i++)
    for(int d=-1;d<=1;d++){
      int tx1=x1[i]+d;
      if(0<=tx1&&tx1<=w)xs.push_back(tx1);
    }
  sort(xs.begin(),xs.end());
  xs.erase(unique(xs.begin(),xs.end()),xs.end());
  for(int i=0;i<n;i++)
    x1[i]=find(xs.begin(),xs.end(),x1[i])-xs.begin();
  return xs.size();
}

int dfs(int x,int y,int dir){
  if(x<0||y<0||x>=W||y>=H)return 0;
  int dx[]={0,0,-1,1},dy[]={-1,1,0,0};
  char ch=mp[y][x];
  mp[y][x]=0;
  if(ch=='^')dir=0;
  else if(ch=='v')dir=1;
  else if(ch=='<')dir=2;
  else if(ch=='>')dir=3;
  return dfs(x+dx[dir],y+dy[dir],dir)+1;
}

int main(){
  cin>>n;
  int x[N],y[N];
  char ch[N];
  for(int i=0;i<n;i++)cin>>x[i]>>y[i]>>ch[i];  
  W=compress(x,1e9)-1;
  H=compress(y,1e9)-1;
  memset(mp,0,sizeof(mp));
