  ans = 0;
  for(int i = 0; i < n; i++){
    for(int j = 0; j < n; j++) fld[iy[j]][ix[j]] = id[j];
    ans = max(ans, solve(ix[i], iy[i], id[i]));
  }
  cout << ans << endl;
  return 0;
}

