	DIR dir;

	for(int start = 0; start < N; start++){
		for(int i = 0; i < N; i++)visited[i] = false;
		visited[start] = true;

		dir = info[start].dir;

		tmp_count = 1;
		next_index = start;

		while(true){

			switch(dir){
			case North:
				while(next_index != -1 && visited[next_index] == true){
					next_index = ADJ[next_index][North];
				}
				break;
			case East:
				while(next_index != -1 && visited[next_index] == true){
					next_index = ADJ[next_index][East];
				}
				break;
			case South:
				while(next_index != -1 && visited[next_index] == true){
					next_index = ADJ[next_index][South];
				}
				break;
			case West:
				while(next_index != -1 && visited[next_index] == true){
					next_index = ADJ[next_index][West];
				}
				break;
			}

			if(next_index == -1)break;

			dir = info[next_index].dir;
			visited[next_index] = true;

			tmp_count++;
		}

		ans = max(ans,tmp_count);
	}

	printf("%d\n",ans);

	return 0;
}

