int compless(vector<int> A,map<int,int> &M){
  sort(A.begin(),A.end());
  int c=0;
  for(int i=0;i<A.size();i++)if(!M.count(A[i]))M[A[i]]=c++;
  return c;
}


int calc(int y,int x,vector<vector<int> >&mp){
  vector<vector<int> > used(h,vector<int>(w,0));
  int res=0;
  while(1){
    res++;
    int i=mp[y][x];
    mp[y][x]=-1;
    while(1){
      y+=dy[i];
      x+=dx[i];
      if(y<0||x<0||x>=w||y>=h)return res;
      if(!used[y][x]++&&mp[y][x]!=-1)break;;
    }
  }
  assert(0);
}

