#include <bits/stdc++.h>

using namespace std;

const string D = "^>v<";
const int dx[] = {0, 1, 0, -1};
const int dy[] = {-1, 0, 1, 0};
const int inf = 1 << 25;
int fld[3000][3000], w, h;
int ans;


void compress(vector<int>& vec)
{
  vector<int> temp = vec;
  sort(temp.begin(), temp.end());
  temp.erase(unique(temp.begin(), temp.end()), temp.end());
  for(int i = 0; i < vec.size(); i++){
    vec[i] = find(temp.begin(), temp.end(), vec[i]) - temp.begin();
  }
}

int solve(int x, int y, int d)
{
  if(x < 0 || w < x || y < 0 || h < y) return 0;
  int nx = x + dx[d], ny = y + dy[d], nd = d;
  if(0 <= nx && nx <= w && 0 <= ny && ny <= h && fld[ny][nx] != -1) nd = fld[ny][nx];
  int hoge = fld[y][x];
  fld[y][x] = -1;
  int ret = solve(nx, ny, nd) + (hoge != -1);
  return ret;
}

