typedef long long int ll;
typedef unsigned long long int ull;
#define BIG_NUM 2000000000
#define MOD 1000000007
#define EPS 0.000000001
using namespace std;


#define NUM 3000

enum DIR{
	North,
	East,
	South,
	West,
};

struct Info{
	int x,y;
	DIR dir;
};

struct Data{
	int x_index,y_index;
	DIR dir;
	bool exist,visited;
};

struct State{
	void set(int arg_y,int arg_x,DIR arg_dir){
		y = arg_y;
		x = arg_x;
		dir = arg_dir;
	}
	int x,y;
	DIR dir;
};

int N;
map<int,int> X_MAP,Y_MAP;
vector<int> Y,X,comp_X[NUM],comp_Y[NUM];
Info info[NUM];
Data data[NUM][NUM]; //2

int main(){

	scanf("%d",&N);

	char buf[2];

	for(int i = 0; i < N; i++){
		scanf("%d %d %s",&info[i].x,&info[i].y,buf);
		X.push_back(info[i].x);
		Y.push_back(info[i].y);
		switch(buf[0]){
		case '^':
			info[i].dir = North;
			break;
		case '>':
			info[i].dir = East;
			break;
		case 'v':
			info[i].dir = South;
			break;
		case '<':
			info[i].dir = West;
			break;
		}
	}

	sort(X.begin(),X.end());
	sort(Y.begin(),Y.end());

	X.erase(unique(X.begin(),X.end()),X.end());
	Y.erase(unique(Y.begin(),Y.end()),Y.end());

	//
	for(int i = 0; i < X.size(); i++){
		X_MAP[X[i]] = i;
	}
	for(int i = 0; i < Y.size(); i++){
		Y_MAP[Y[i]] = i;
	}

	int size_X = (int)X.size(),size_Y = (int)Y.size();

	for(int i = 0; i < size_X; i++){
		for(int k = 0; k < size_Y; k++)data[i][k].exist = false;
	}

	//
	for(int i = 0; i < N; i++){
		data[Y_MAP[info[i].y]][X_MAP[info[i].x]].exist = true;
		data[Y_MAP[info[i].y]][X_MAP[info[i].x]].dir = info[i].dir;

		comp_Y[Y_MAP[info[i].y]].push_back(X_MAP[info[i].x]); //YX
		comp_X[X_MAP[info[i].x]].push_back(Y_MAP[info[i].y]); //XY
	}

	for(int i = 0; i < size_Y; i++)sort(comp_Y[i].begin(),comp_Y[i].end());
	for(int i = 0; i < size_X; i++)sort(comp_X[i].begin(),comp_X[i].end());

	//
	int left,right,m;
	for(int row = 0; row < size_Y; row++){
		for(int col = 0; col < size_X; col++){
			if(data[row][col].exist == false)continue;

			//y == rowx
			left = 0,right = comp_Y[row].size()-1,m = (left+right)/2;
			while(left <= right){
				if(comp_Y[row][m] == col){
					data[row][col].y_index = m;
					break;
				}else if(comp_Y[row][m] > col){
					right = m-1;
				}else{
					left = m+1;
				}
				m = (left+right)/2;
			}

			//x == coly
			left = 0,right = comp_X[col].size()-1,m = (left+right)/2;
			while(left <= right){
				if(comp_X[col][m] == row){
					data[row][col].x_index = m;
					break;
				}else if(comp_X[col][m] > row){
					right = m-1;
				}else{
					left = m+1;
				}
				m = (left+right)/2;
			}
		}
	}


	int ans = 0;
	int next_y,next_x;
	State state;

	//
	for(int start = 0; start < N; start++){

		for(int i = 0; i < size_X; i++){
			for(int k = 0; k < size_Y; k++)data[i][k].visited = false;
		}

		int tmp_count = 1;
		data[Y_MAP[info[start].y]][X_MAP[info[start].x]].visited = true;
		state.set(Y_MAP[info[start].y],X_MAP[info[start].x],info[start].dir);

		while(true){

			next_y = BIG_NUM;
			next_x = BIG_NUM;

			//
			switch(state.dir){
			case North:
				for(int tmp_y = data[state.y][state.x].x_index-1; tmp_y >= 0; tmp_y--){
					if(data[comp_X[state.x][tmp_y]][state.x].exist == true && data[comp_X[state.x][tmp_y]][state.x].visited == false){
						next_y = comp_X[state.x][tmp_y];
						next_x = state.x;
						break;
					}
				}
				break;
			case East:
				for(int tmp_x = data[state.y][state.x].y_index+1; tmp_x <= comp_Y[state.y].size()-1; tmp_x++){
					if(data[state.y][comp_Y[state.y][tmp_x]].exist == true && data[state.y][comp_Y[state.y][tmp_x]].visited == false){
						next_y = state.y;
						next_x = comp_Y[state.y][tmp_x];
						break;
					}
				}
				break;
			case South:
				for(int tmp_y = data[state.y][state.x].x_index+1; tmp_y <= comp_X[state.x].size()-1; tmp_y++){
					if(data[comp_X[state.x][tmp_y]][state.x].exist == true && data[comp_X[state.x][tmp_y]][state.x].visited == false){
						next_y = comp_X[state.x][tmp_y];
						next_x = state.x;
						break;
					}
				}
				break;
			case West:
				for(int tmp_x = data[state.y][state.x].y_index-1; tmp_x >= 0; tmp_x--){
					if(data[state.y][comp_Y[state.y][tmp_x]].exist == true && data[state.y][comp_Y[state.y][tmp_x]].visited == false){
						next_y = state.y;
						next_x = comp_Y[state.y][tmp_x];
						break;
					}
				}
				break;
			}


			if(next_y == BIG_NUM || next_x == BIG_NUM)break;

			data[next_y][next_x].visited = true;
			state.set(next_y,next_x,data[next_y][next_x].dir);
			tmp_count++;
		}

		ans = max(ans,tmp_count);
	}

	printf("%d\n",ans);

	return 0;
}

