            }
            else if(cd == '<'){
                auto itr = sy[cy].lower_bound({cx,cd});
                if(itr == sy[cy].begin()) break;
                --itr;
                cx = itr->fi;
                cd = itr->se;

            }
            else if(cd == 'v'){
                auto itr = sx[cx].lower_bound({cy,cd});
                if(itr == sx[cx].end()) break;

                cy = itr->fi;
                cd = itr->se;
            }
            else if(cd == '^'){
                auto itr = sx[cx].lower_bound({cy,cd});
                if(itr == sx[cx].begin()) break;
                --itr;
                cy = itr->fi;
                cd = itr->se;
            }
            else assert(false);
        }

        ans = max(ans, tmp);
    }
    cout << ans << endl;
    return 0;
}

