using namespace std;

struct point{
	int id;
	int a, b;
	point(){}
	point(int i, int x, int y){
		this->id = i, this->a = x, this->b = y;
	}
	bool operator<(const point& ope)const{
		return a < ope.a || (a == ope.a && b < ope.b);
	}
};

int n;
int x[3005], y[3005], d[3005];
int next[3005][4];
point XY[3005], YX[3005];

void make_link(point p[], int dir)
{
	int prev = 1000000005;
	for(int i = 0; i < n; i++){
		if(prev == p[i].a){
			next[p[i-1].id][dir] = p[i].id;
		}
		else if(i != 0){
			next[p[i-1].id][dir] = -1;
		}
		prev = p[i].a;
	}
	next[p[n-1].id][dir] = -1;
}

int search(int s)
{
	int next2[3005][4];
	for(int i = 0; i < n; i++){
		for(int j = 0; j < 4; j++){
			next2[i][j] = next[i][j];
		}
	}

	int ret = 0, pos = s;
	while(1){
		for(int dir = 0; dir < 4; dir++){
			if(next2[pos][dir] == -1) continue;
			next2[next2[pos][dir]][(dir+2)%4] = next2[pos][(dir+2)%4];
		}
		pos = next2[pos][d[pos]];
		ret++;
		if(pos == -1) return ret;
	}
}

int main(void)
{
	cin >> n;

	char c;
	for(int i = 0; i < n; i++){
		cin >> x[i] >> y[i] >> c;
		if(c == '>') d[i] = 0;
		if(c == '^') d[i] = 1;
		if(c == '<') d[i] = 2;
		if(c == 'v') d[i] = 3;
	}

	for(int i = 0; i < n; i++){
		XY[i] = point(i, x[i], y[i]);
		YX[i] = point(i, y[i], x[i]);
	}

	sort(XY, XY+n); make_link(XY, 3);
	reverse(XY, XY+n); make_link(XY, 1);
	sort(YX, YX+n); make_link(YX, 0);
	reverse(YX, YX+n); make_link(YX,  2);

	int ans = 0;
	for(int i = 0; i < n; i++){
		ans = max(ans, search(i));
	}

	cout << ans << endl;
}

#include <iostream>
#include <algorithm>
#include <vector>
#include <map>

void solve() {
    int n;
    std::cin >> n;

    std::vector<int> ds(n);
    std::map<int, std::vector<std::pair<int, int>>> xby, yby;

    for (int i = 0; i < n; ++i) {
        int x, y;
        char c;
        std::cin >> x >> y >> c;

        int d;
        for (d = 0; d < 4; ++d) {
            if (c == "><v^"[d]) break;
        }

        ds[i] = d;
        xby[x].emplace_back(y, i);
        yby[y].emplace_back(x, i);
    }

    std::vector<std::vector<int>> oto(n, std::vector<int>(4, -1));
    for (auto& p : xby) {
        auto& ys = p.second;
        std::sort(ys.begin(), ys.end());

        for (int i = 0; i + 1 < (int)ys.size(); ++i) {
            int u = ys[i].second, v = ys[i + 1].second;
            oto[u][2] = v;
            oto[v][3] = u;
        }
    }

    for (auto& p : yby) {
        auto& xs = p.second;
        std::sort(xs.begin(), xs.end());

        for (int i = 0; i + 1 < (int)xs.size(); ++i) {
            int u = xs[i].second, v = xs[i + 1].second;
            oto[u][0] = v;
            oto[v][1] = u;
        }
    }

    int ans = 0;
    for (int s = 0; s < n; ++s) {
        auto to = oto;
        int cnt = 0;

        int v = s;
        while (v != -1) {
            int p = to[v][0], q = to[v][1];
            if (p != -1) to[p][1] = q;
            if (q != -1) to[q][0] = p;

            p = to[v][2], q = to[v][3];
            if (p != -1) to[p][3] = q;
            if (q != -1) to[q][2] = p;

            v = to[v][ds[v]];
            ++cnt;
        }

        ans = std::max(ans, cnt);
    }

    std::cout << ans << std::endl;
}

int main() {
    std::cin.tie(nullptr);
    std::cout.tie(nullptr);
    std::ios::sync_with_stdio(false);

    solve();

    return 0;
}

