int solve(int si){
  bool used[MAX_N]={};
  set< P > sx[MAX_N],sy[MAX_N];
  for(int i=0;i<n;i++){
    if(i==si)continue;
    sx[ px[i] ].insert( P(py[i],i) );
    sy[ py[i] ].insert( P(px[i],i) );
  }
  int res=1;
  int x=px[si],y=py[si];
  char dir=ch[si];

  set< P > :: iterator it;
  while(1){
    P next;
    if(dir=='v'){
      it= sx[x].lower_bound( P(y,0) );
      if(it==sx[x].end())break;
      next=*it;
    }else if(dir=='^'){
      it= sx[x].lower_bound( P(y,0) );
      if(it==sx[x].begin())break;
      it--;
      next=*it;
    }else if(dir=='>'){
      it= sy[y].lower_bound( P(x,0) );
      if(it==sy[y].end())break;
      next=*it;      
    }else if(dir=='<'){
      it= sy[y].lower_bound( P(x,0) );
      if(it==sy[y].begin())break;
      it--;
      next=*it;
    }
    res++;
    int id=next.second;
    x=px[id];
    y=py[id];
    dir=ch[id];
    sx[ x ].erase(P(y,id));
    sy[ y ].erase(P(x,id));
  }
  return res;
}

