			for(int k = 0; k < size_Y; k++)data[i][k].visited = false;
		}

		int tmp_count = 1;
		data[Y_MAP[info[start].y]][X_MAP[info[start].x]].visited = true;
		state.set(Y_MAP[info[start].y],X_MAP[info[start].x],info[start].dir);

		while(true){

			next_y = BIG_NUM;
			next_x = BIG_NUM;

			//
			switch(state.dir){
			case North:
				for(int tmp_y = state.y-1; tmp_y >= 0; tmp_y--){
					if(data[tmp_y][state.x].exist == true && data[tmp_y][state.x].visited == false){
						next_y = tmp_y;
						next_x = state.x;
						break;
					}
				}
				break;
			case East:
				for(int tmp_x = state.x+1; tmp_x <= size_X-1; tmp_x++){
					if(data[state.y][tmp_x].exist == true && data[state.y][tmp_x].visited == false){
						next_y = state.y;
						next_x = tmp_x;
						break;
					}
				}
				break;
			case South:
				for(int tmp_y = state.y+1; tmp_y <= size_Y-1; tmp_y++){
					if(data[tmp_y][state.x].exist == true && data[tmp_y][state.x].visited == false){
						next_y = tmp_y;
						next_x = state.x;
						break;
					}
				}
				break;
			case West:
				for(int tmp_x = state.x-1; tmp_x >= 0; tmp_x--){
					if(data[state.y][tmp_x].exist == true && data[state.y][tmp_x].visited == false){
						next_y = state.y;
						next_x = tmp_x;
						break;
					}
				}
				break;
			}

			if(next_y == BIG_NUM || next_x == BIG_NUM)break;

			data[next_y][next_x].visited = true;
			state.set(next_y,next_x,data[next_y][next_x].dir);
			tmp_count++;
		}

		ans = max(ans,tmp_count);
	}

	printf("%d\n",ans);

	return 0;
}

