			int pre = -1;
			int tmp = 0;
			while (true) {
				pre = cur;
				if (d[cur] == 'v') {
					cur = UP[cur];
				} else if (d[cur] == '^') {
					cur = DOWN[cur];
				} else if (d[cur] == '>') {
					cur = RIGHT[cur];
				} else if (d[cur] == '<') {
					cur = LEFT[cur];
				} else {
					throw new AssertionError();
				}
				++tmp;
				if (cur == -1)
					break;
				else {
					erase(pre, UP, DOWN, RIGHT, LEFT);
				}
			}
			ans = Math.max(ans, tmp);
		}
		System.out.println(ans);
	}

	void erase(int id, int[] up, int[] down, int[] right, int[] left) {
		if (id == -1)
			return;
		if (down[id] != -1)
			up[down[id]] = up[id];
		if (up[id] != -1)
			down[up[id]] = down[id];
		if (left[id] != -1)
			right[left[id]] = right[id];
		if (right[id] != -1)
			left[right[id]] = left[id];
		up[id] = -1;
		down[id] = -1;
		right[id] = -1;
		left[id] = -1;
	}

	void shrink(long[] a) {
		long[][] b = new long[a.length][2];
		for (int i = 0; i < a.length; ++i) {
			b[i][0] = a[i];
			b[i][1] = i;
		}
		Arrays.sort(b, new Comparator<long[]>() {
			@Override
			public int compare(long[] arg0, long[] arg1) {
				return Long.compare(arg0[0], arg1[0]);
			}
		});
		int p = 0;
		for (int i = 0; i < b.length; ++i) {
			if (i - 1 >= 0 && b[i][0] != b[i - 1][0]) {
				++p;
			}
			a[(int) b[i][1]] = p;
		}
	}

	void tr(Object... objects) {
		System.out.println(Arrays.deepToString(objects));
	}

	public static void main(String[] args) {
		new Main().run();
	}
}

