ll po(ll a, ll b) {
  ll res = 1;
  for(int i=0;i<b;++i) {
    res *= a;
  }
  return res;
}

int digit(ll n) {
  int res = 0;
  while(n > 0) {
    res++;
    n /= 10;
  }
  return res;
}

