    if(na[l-1][0]<0)
        for(int i=0;i<=l-1;i++)
            na[i][0]=0;
    for(int i=l-1;i>=0;i--){
        if(v1[na[i][0]]){
            for(int j=0;j<i;j++)
                na[j][0]=0;
            while(v1[na[i][0]]){
                na[i][0]++;
            }
        }
        if(na[i][0]==10){
            while(na[i][0]==10){
                v1[na[i+1][0]]--;
                na[i+1][0]++;
                na[i][0]=0;
                i++;
            }
        }
        v1[na[i][0]]++;
    }
