    if(na[l-1][3]>9){
        num[3]=a;
        for(int i=0;i<l;i++)
            na[i][3]=9;
    }
    for(int i=l-1;i>=0;i--){
        if(v1[na[i][3]]){
            for(int j=0;j<i;j++)
                na[j][3]=9;
            while(v1[na[i][3]]){
