string s;

int64 ten(int64 x, int64 d) {
  REP(i, d) x *= 10;
  return x;
}

int64 stoi_my(string s) {
  int64 ret = 0;
  REP(i, s.size()) ret = ret*10 + s[i]-'0';
  return ret;
}

int64 score(int64 a) {
  int64 ss = stoi_my(s);
  return min(abs(ss-a), ten(1, s.size()) - abs(ss-a));
}

int main(void) {
  vector<int64> v(10, 0);
  cin >> s;
  REP(i, s.size()) {
    v[i] = i+1;
  }
  sort(all(v));
  int64 ans = 0;
  do {
    string res(s.size(), '.');
    REP(i, v.size()) {
      if (v[i] != 0) {
        res[v[i]-1] = '0' + i;
      }
    }
    if (score(ans) <= score(stoi_my(res))) {
      if (ans > stoi_my(res))
        ans = stoi_my(res);
    }
  } while (next_permutation(all(v)));
  cout << setfill('0') << setw(s.size()) << ans << endl;
}

