  return min(abs(ss-a), ten(1, s.size()) - abs(ss-a));
}

int64 dfs(int64 bit, int64 d, int64 now=0) {
  if (d == s.size()) {
    return now;
  }
  int64 dd = s[d] - '0';
  int64 res = stoi(s);
  int64 cnt = 0;
  REP(i, 10) {
    if (dd-i >= 0 && (bit >> (dd-i) & 1) == 0) {
      int64 ret = dfs(bit | 1 << (dd-i), d+1, now * 10 + dd-i);
      if (score(res) < score(ret))
        res = ret;
      cnt++;
    }
    if (dd+i <= 9 && (bit >> (dd+i) & 1) == 0) {
      int64 ret = dfs(bit | 1 << (dd+i), d+1, now * 10 + dd+i);
      if (score(res) < score(ret))
        res = ret;
      cnt++;
    }
    if (cnt > 5) break;
  }
  return res;
}

