}

int64 score(int64 a) {
  int64 ss = stoi(s);
  return min(abs(ss-a), ten(1, s.size()) - abs(ss-a));
}

int64 calc(int64 h) {
  int64 used = 1 << h;
  int64 res = h;
  int64 d = 1;
  for (; d < s.size() && (used >> (s[d]-'0') & 1) == 0; d++) {
    res = res * 10 + s[d]-'0';
    used |= 1 << (s[d]-'0');
  }
  int64 r = stoi(s);
  if (d < s.size() - 1) {
    REP(i, 10) {
      if (used >> i & 1) continue;
      int64 ret = res * 10 + i;
      int64 x = ret;
      int64 cnt = d+1;
      used |= 1 << i;
      for (int64 j = 9; j>= 0; j--) {
        if (cnt == s.size()) break;
        if (used >> j & 1) continue;
        x = x * 10 + j;
        cnt++;
      }
      if (score(r) < score(x)) r = x;
      cnt = d+1; x = ret;
      REP(j, 10) {
        if (cnt == s.size()) break;
        if (used >> j & 1) continue;
        x = x * 10 + j;
        cnt++;
      }
      used ^= 1<< i;
      if (score(r) < score(x)) r = x;
    }
  } else {
    r = res;
  }
  return r;
}

