void rec(int n, string t){
    // cout << n << " " << t << endl;
    if(n == 0){
        ll d = abs(stol(s)-stol(t));
        if(min(d, maxi-d) > diff){
            ans = t;
            diff = min(d, maxi-d);
        }else if(min(d, maxi-d) == diff && stol(ans)>stol(t)){
            ans = t;
        }
        return;
