}


bool used[11];
int N;
ll pow10_;
string compute(string s){
  string ans = "";
  memset(used,false,sizeof(used));
  rep(i,(int)s.size()) {
    if( !used[s[i]-'0'] ) {
      used[s[i]-'0'] = true;
      ans += string(1,s[i]);
    } else {
      int prefix = (atoi)(s.substr(0,i+1).c_str());
      int L = prefix, R = prefix;

      int which = -1;
      while(1) {
	string tmp = s;
	--L;
	if( L < 0 ) {
	  L = pow10_ - 1;
	}
	setter(tmp,L); 
	if( isValid(to_string1(L)) ) {
	  which = 1;
	  ans = to_string1(L);
	  ans = string(i-(int)ans.size()+1,'0') + ans;
	  memset(used,false,sizeof(used));
	  rep(j,(int)ans.size()) used[ans[j]-'0'] = true;
	  break;
	}


	++R;	
	if( R >= pow10_ ) {
	  R = 0;
	}
	tmp = s;
	setter(tmp,R);
	if( isValid(to_string1(R)) ) {
	  which = 0;
	  ans = to_string1(R);
	  ans = string(i-(int)ans.size()+1,'0') + ans;
	  memset(used,false,sizeof(used));
	  rep(j,(int)ans.size()) used[ans[j]-'0'] = true;
	  break;
	}

      }
      assert( which != -1 );
      if( which == 1 ) { // max
	int cur = 9;
	while( (int)ans.size() < N ) {
	  if( used[cur] ) { --cur; continue; }
	  used[cur] = true;
	  ans += string(1,(char)(cur+'0'));
	  --cur;
	}
      } else { // min
      	int cur = 0;
	while( (int)ans.size() < N ) {
	  if( used[cur] ) { ++cur; continue; }
	  used[cur] = true;
	  ans += string(1,(char)(cur+'0'));
	  ++cur;
	}
      }
      break;
    }
  }
  return ans;
}


ll getDiffer(string olds,string news) {
  ll o = (atoll)(olds.c_str());
  ll n = (atoll)(news.c_str());
  return min(llabs(o-n),pow10_-llabs(o-n));
}


bool is(int i,int keta){
  memset(used,false,sizeof(used));
  string s = to_string1(i);
  s = string(keta-(int)s.size(),'0') + s;
  rep(i,(int)s.size()) {
    if( used[s[i]-'0'] ) return false;
    used[s[i]-'0'] = true;
  }
  return true;
}

string bf(string s){
 ll answ = -1;
  ll maxi = -1; 
  rep(i,(int)pow10_){
    if(!is(i,(int)s.size())) continue;
    ll v = getDiffer(s,to_string1(i));
    //cout << i <<" -> " << v << endl;
    if( maxi  == -1) {
      maxi = v;
      answ = (ll)i;
    } else if( maxi <= v ) {
      if( maxi == v ) {
	if( answ > i ) answ= i;
      } else {
	answ = i;
      }
      maxi = v;
    }
  }
  return to_string1(answ);
}

