void saiki(int idx,ll num) {
	if (idx == N) {
		if (ma < min(abs(n - num), (ll)pow(10, N) - abs(n - num))) {
			ma = max(ma, min(abs(n - num), (ll)pow(10, N) - abs(n - num)));
			ans = num;
		}
		else if (ma == min(abs(n - num), (ll)pow(10, N) - abs(n - num))) {
			ans = min(ans,num);
		}
		return;
	}
	REP(i, 10) {
		if (!(key&(1 << i))) {
			key |= (1 << i);
			saiki(idx + 1, num * 10 + i);
			key &= ~(1 << i);
		}
	}
}
