ll old, d = 0, ans;

void dfs(int rem, int mask, ll p) {
  if (rem == 0) {
    ll nd = min(abs(p - old), (ll)pow(10, sz) - abs(p - old));
    if (nd < d) return;
    if (nd == d) ans = min(ans, p);
    else ans = p;
    d = nd;
    return;
  }
  for (int i = 0; i < 10; i++) {
    if ((mask >> i & 1) == 0) {
      dfs(rem - 1, mask | (1 << i), p * 10 + i);
    }
  }
}

