ll sll(string s) {
	ll ret = 0;
	REP(i, s.size()) {
		ret *= 10;
		ret += s[i] - '0';
	}
	return ret;
}
ll dist(string s, string t) {
	return abs(sll(s) - sll(t));
}

int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);
	string s;
	cin >> s;
	int n = s.size();
	string ans;
	ll cmax = -INF;
	REP(i, 1 << 10) {
		int cnt = 0;
		REP(j, 10)if (i&(1 << j))cnt++;
		if (cnt != n)continue;
		string t;
		REP(j, 10) {
			if (i&(1 << j))t += char('0' + j);
		}
		sort(ALL(t));
		do {
			ll a = min(dist(s, t),pow(10,n)-dist(s,t));
			if (a > cmax||(a==cmax&&sll(ans)>sll(t))) {
				cmax = a;
				ans = t;
			}
		} while (next_permutation(ALL(t)));
	}
	cout << ans << endl;

	return 0;
}

